import React from 'react';
import './E1rmDisplay.scss';
import { connect } from 'react-redux';
import { calculateE1RM } from '../utils/calculations.js';
import { getEstimatedOneRepMax } from '../redux/slices/currentWorkout';

const E1rmDisplay = props => {
  const weight = Math.round(
    calculateE1RM(props.set.weight, props.set.reps, props.set.rpe)
  );
  return (
    <div className="e1rm-display">
      <b>E1RM:</b> {weight} lbs
    </div>
  );
};

const mapStateToProps = state => {
  estimateOneRepMax: getEstimatedOneRepMax(state)
}

export default connect(
  mapStateToProps,
  null
)(E1rmDisplay);
