import React from 'react';
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { getE1RMHistoryForLift } from '../redux/slices/liftHistory';
import {
  ResponsiveContainer,
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip
} from 'recharts';
import './SingleMeasurementView.scss';

const SingleMeasurementView = props => {
  const liftName = props.match.params.dataItem;
  const liftHistory = useSelector(state =>
    getE1RMHistoryForLift(state, { liftName: liftName })
  );
  function formatDate(timestamp) {
    let date = new Date(timestamp);
    return date.getMonth() + '/' + date.getDate();
  }
  liftHistory.forEach(entry => (entry.timestamp = formatDate(entry.timestamp)));
  return (
    <div id="single-measurement-view">
      <Link to="/view" className="upper-left">
        <button type="button" className="arrow-button">
          &larr;
        </button>
      </Link>
      <h1 className="lift-title">{liftName.toUpperCase()}</h1>
      <ResponsiveContainer width="90%">
        <LineChart data={liftHistory}>
          <Line dataKey="e1rm" stroke="rgba(255, 255, 255, .8)" />
          <XAxis dataKey="timestamp" stroke="rgba(255, 255, 255, 1)" />
          <YAxis
            type="number"
            domain={['dataMin - 50', 'dataMax + 50']}
            width={35}
            stroke="rgba(255, 255, 255, 1)"
          />
          <Tooltip contentStyle={{ backgroundColor: 'rgba(0, 0, 0, .1)' }} />
        </LineChart>
      </ResponsiveContainer>
      <table id="e1rm-table">
        <thead>
          <tr>
            <th>DATE</th>
            <th>E1RM</th>
          </tr>
        </thead>
        <tbody>
          {liftHistory.map(entry => (
            <tr>
              <td>{entry.timestamp}</td>
              <td>{entry.e1rm}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default SingleMeasurementView;
