import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import './RestTimer.scss';
import { prettyDisplayTime } from '../utils/displayHelpers';
import { useInterval } from '../utils/customHooks';

const RestTimer = props => {
  const timer = useSelector(
    state => state.currentWorkout.restTimer
  );
  const [secondsRemaining, setSecondsRemaining] = useState(null);
  useInterval(() => (setSecondsRemaining((Date.now() + timer.duration - timer.timeStarted) / 1000)), 1000);

  console.log(secondsRemaining);
  if (secondsRemaining) {
  return (
    <div className="rest-timer">
      REST COUNTDOWN: {prettyDisplayTime(secondsRemaining)}
    </div>
  );
  } else {
    return null
  }
};

export default RestTimer;
