import React, { useState } from 'react';
import './MeasurementForm.scss';

const MeasurementForm = props => {
  const [measurement, setMeasurement] = useState(5);
  function onSubmit(event) {
    event.preventDefault();
    props.closeModal();
  }

  return (
    <div id="measurement-form-component">
      <form id="measurement" className="form-one" onSubmit={onSubmit}>
        <label for="measurement-input">{props.type}:</label>
        <input
          id="measurement-input"
          type="number"
          required={true}
          value={measurement}
          onChange={event => setMeasurement(event.target.value)}
        />
        <button
          type="button"
          className="form-button"
          onClick={props.closeModal}
        >
          CANCEL
        </button>
        <button type="submit" className="form-button">
          ENTER
        </button>
      </form>
    </div>
  );
};

export default MeasurementForm;
