import React from 'react';

import './Modal.scss';

const Modal = props => {
  if (props.showModal) {
    return (
      <div className="modal" id={props.id}>
          {props.toggleButton && (
            <button type="button" onClick={props.toggle} id="close-modal-button">
                X
            </button>
          )}
              {props.children}
          </div>
    );
  } else {
    return null;
  }
};

Modal.defaultProps = {
  toggleButton: true
};

export default Modal;
