import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import LiftLog from './LiftLog';
import Modal from './Modal';
import Backdrop from './Backdrop';
import './Workout.scss';

const Workout = props => {
  const [isBlurred, setIsBlurred] = useState(true);
  const [newSetPopup, setNewSetPopup] = useState(true);
  const toggleModal = () => {
    setNewSetPopup(!newSetPopup);
    setIsBlurred(!isBlurred);
  };
  let test = isBlurred ? 'blurred' : '';

  return (
    <React.Fragment>
      <div id="workout-screen" className={test}>
        <Link to="/" class="upper-left">
          <button type="button" class="arrow-button">
            &larr;
          </button>
        </Link>
        <button type="button" class="upper-right button-underline">
          TIMES
        </button>
        <LiftLog toggleModal={toggleModal} />
      </div>
      {newSetPopup && (
        <Modal toggle={toggleModal} id="new-set-modal">
        </Modal>
      )}
      {newSetPopup && <Backdrop />}
    </React.Fragment>
  );
};

export default Workout;
