import React, { useState } from 'react';

const LiftLog = () => {
  const [lifts, setLifts] = useState([
    { name: 'deadlift' },
    { name: 'squat' },
    { name: 'bench press' }
  ]);

  const [currentLiftIndex, setCurrentLiftIndex] = useState(0);
  function selectPreviousLift() {
    if (currentLiftIndex === lifts.length) {
      setCurrentLiftIndex(0);
    } else {
      setCurrentLiftIndex(currentLift - 1);
    }
  }

  return (
    <div id="lift-log">
      <LiftSelector currentLift={lifts[currentLiftIndex]} />
    </div>
  );
};

const LiftSelector = props => {
  return (
    <div id="lift-selector">
      <button type="button" className="arrow-button">
        &larr;
      </button>
      <h2>{props.currentLift.name}</h2>
      <button type="button" className="arrow-button">
        &#65291;
      </button>
    </div>
  );
};

export default LiftLog;
