import React, { useState } from 'react';
import './NewLift.scss';

const NewLift = props => {
  const [selectedLift, setSelectedLift] = useState('SQUAT');
  const liftList = ['SQUAT', 'BENCH PRESS', 'DEADLIFT'];
  return (
      <form id="new-lift">
        {liftList.map(lift => {
          return (
            <>
            <input
              type="radio"
              value={lift}
              className="lift-option"
              checked={lift === selectedLift}
              onChange={() => setSelectedLift(lift)}
            />
            <label className="lift-option">
                {lift}
            </label>
            </>
          );
        })}
            <input type="button" className="button-one cancel-button" value="CANCEL" onClick={props.toggleModal}/>
            <input type="submit" className="button-one" value="ADD LIFT" />
      </form>
  );
};

export default NewLift;
