import React, { useState } from 'react';
import RestTimer from './RestTimer';
import './TimesDisplay.scss';

const TimesDisplay = props => {
  const [workoutSeconds, setWorkoutSeconds] = useState(props.workoutTimer);

  return (
    <>
      <button
        type="button"
        onClick={props.closeModal}
        className="upper-right button-underline"
      >
          TIMES
    </button>
    {props.restTimerStarted && (
      <RestTimer secondsRemaining={props.restTimerSeconds} />
    )}
    <div>
        Workout Timer: {workoutSeconds}
    </div>
    </>
  );
};

export default TimesDisplay;
