import React, { useState } from 'react';
import './MeasurementForm.scss';

const MeasurementForm = props => {
  const [measurement, setMeasurement] = useState(5);
  function onSubmit(event) {
    event.preventDefault();
    props.closeModal();
  }

  return (
    <form id="measurement-form" className="form-one" onSubmit={onSubmit}>
      <label for="measurement-input" className="span-2-columns">{props.type}:</label>
      <input
        id="measurement-input"
        type="number"
        required={true}
        value={measurement}
        onChange={event => setMeasurement(event.target.value)}
      />
      <button type="button" className="form-button span-3-columns" onClick={props.closeModal}>
        CANCEL
      </button>
      <button type="submit" className="form-button span-3-columns">
        ENTER
      </button>
    </form>
  );
};

export default MeasurementForm;
