import React from 'react';
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { getE1RMHistoryForLift } from '../redux/slices/liftHistory';
import {
  ResponsiveContainer,
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip
} from 'recharts';
import './SingleMeasurementView.scss';

const SingleMeasurementView = props => {
  const measurementName = props.match.params.dataItem;
  const type = props.location.state.type;
  const unit = props.location.state.unit;
  let selector;
  let selectorProps;
  if (type === 'lift') {
    if (unit === 'e1rm') {
      selector = getE1RMHistoryForLift;
      selectorProps = { liftName: measurementName };
    }
  }
  const measurementHistory = useSelector(state =>
    selector(state, selectorProps)
  );
  function formatDate(timestamp) {
    let date = new Date(timestamp);
    return date.getMonth() + '/' + date.getDate();
  }
  measurementHistory.forEach(
    entry => (entry.timestamp = formatDate(entry.timestamp))
  );
  return (
    <div id="single-measurement-view">
      <Link to="/view" className="upper-left">
        <button type="button" className="arrow-button">
          &larr;
        </button>
      </Link>
      <h1 className="lift-title">{measurementName.toUpperCase()}</h1>
      <div className="graph-area">
        <ResponsiveContainer>
          <LineChart
            data={measurementHistory}
            margin={{ top: 0, right: 5, bottom: 0, left: 0 }}
          >
            <Line dataKey="e1rm" stroke="rgba(255, 255, 255, .8)" />
            <XAxis dataKey="timestamp" stroke="rgba(255, 255, 255, 1)" />
            <YAxis
              type="number"
              domain={['dataMin - 50', 'dataMax + 50']}
              width={35}
              stroke="rgba(255, 255, 255, 1)"
            />
            <Tooltip
              contentStyle={{
                backgroundColor: 'rgba(50, 50, 50, .85)',
                border: '1px solid rgba(255, 255, 255, .3)'
              }}
            />
          </LineChart>
        </ResponsiveContainer>
      </div>
      <table id="e1rm-table">
        <thead>
          <tr>
            <th>DATE</th>
            <th>{unit.toUpperCase()}</th>
          </tr>
        </thead>
        <tbody>
          {measurementHistory.map(entry => (
            <tr>
              <td>{entry.timestamp}</td>
              <td>{entry.e1rm}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default SingleMeasurementView;
