import React from 'react';
import { useSelector } from 'react-redux';
import './RestTimer.scss';
import { prettyDisplayTime } from '../utils/displayHelpers';
import { useInterval } from '../utils/customHooks';

const RestTimer = props => {
  const timerStarted = useSelector(
    state => state.currentWorkout.restTimer.timeStarted
  );
  let secondsRemaining = 0;
  useInterval(() => (secondsRemaining = Date.now() - timerStarted), 1000);

  console.log(timerStarted);
  console.log(secondsRemaining);
  if (secondsRemaining) {
  return (
    <div className="rest-timer">
      REST COUNTDOWN: {prettyDisplayTime(secondsRemaining)}
    </div>
  );
  } else {
    return null
  }
};

export default RestTimer;
